import "./style.css";

import React, { useState } from "react";

const ChatbotModal = ({ closeFunc, feedbackId }) => {
  const [stage, setStage] = useState(1);
  const [question, setQuestions] = useState("");

  const getQuestion1 = () => {
    apiInstance
      .get(`/posts/questions/${feedbackId}`, {
        params: {
          number: "1",
        },
      })
      .then((resp) => {
        const {
          data: { data },
        } = resp;
        setQuestions(data);
        setStage(2);
      })
      .catch((err) => {
        console.log(err.response.data.error);
      });
  };

  return (
    <div className="modal__container">
      <div className="modal__content">
        {stage === 1 ? (
          <>
            <div className="modal__top">
              <h2>Thank you for reporting a case</h2>
              <span>We would like to ask you a few questions </span>
            </div>
            <div className="modal__button" onClick={() => setStage(2)}>
              Next
            </div>
          </>
        ) : stage === 2 ? (
          <>
            <div className="modal__top">
              <h2>Question 1</h2>
              <span> {question} </span>
            </div>
            <div
              className="modal__button"
              onClick={() => {
                setStage(3);
              }}
            >
              Next
            </div>
          </>
        ) : stage === 3 ? (
          <>
            <div className="modal__top">
              <h2>Question 2</h2>
              <span> {question} </span>
            </div>
            <div className="modal__button" onClick={() => setStage(4)}>
              Next
            </div>
          </>
        ) : stage === 4 ? (
          <>
            <div className="modal__top">
              <h2>Question 3</h2>
              <span> {question} </span>
            </div>
            <div className="modal__button" onClick={() => setStage(5)}>
              Next
            </div>
          </>
        ) : stage === 5 ? (
          <>
            <div className="modal__top">
              <h2>Question 4</h2>
              <span> {question} </span>
            </div>
            <div className="modal__button" onClick={() => setStage(5)}>
              Next
            </div>
          </>
        ) : (
          <>
            <div className="modal__top">
              <h2>Thank you!</h2>
            </div>
            <div className="modal__button" onClick={closeFunc}>
              Close
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatbotModal;
