import "../styles/interactions.css";

import React, { useState } from "react";

import ArrowDownIcon from "../assests/ArrowDownIcon";
import ChartCanva from "../components/chart/Chart";
import DashboardLayout from "../components/dashboardLayout";
import { apiInstance } from "../utils/utils";

const Interactions = () => {
  const [data, setData] = useState([]);
  const [type, setType] = useState("rape");

  const getThreatLocations = (type) => {
    apiInstance
      .get("/posts/threat-locations", {
        params: {
          type,
        },
      })
      .then((resp) => {
        const {
          data: { data },
        } = resp;
        setData(data);
      })
      .catch((err) => {
        console.log(err.response.data.error);
      });
  };

  return (
    <DashboardLayout admin>
      <div className="interactions__container">
        <h1 className="interactions__header">User Interactions</h1>

        <div className="interactions__div">
          <div className="interactions__header">
            Highest Threat Location
            <div className="interactions__dropdown">
              <span>{type} </span>
              <span>
                <ArrowDownIcon />
              </span>
              <div className="interactions__items">
                <div className="interactions__Item">Rape</div>
                <div className="interactions__Item">Robbery</div>
                <div className="interactions__Item">Corruption</div>
              </div>
            </div>
          </div>
          <div className="interactions__body">
            <ChartCanva />
          </div>
        </div>
        <div className="interactions__div">
          <div className="interactions__header">Users Heatmap</div>
          <div className="interactions__body"></div>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Interactions;
