  ArcElement,
  CategoryScale,
  Chart,
  Chart as ChartJS,
  Filler,
  Legend,
  LineElement,
  LinearScale,
  PointElement,
  TimeScale,
  Tooltip,
} from "chart.js";
import React, { useEffect, useRef, useState } from "react";

import { Doughnut } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  TimeScale,
  ArcElement,
  Filler,
  Tooltip,
  Legend
);

const ChartCanva = ({ dataArray, keyArray }) => {
const [data, setData] = useState();
const [key, setKey] = useState();

  useEffect(() => {
  if (data||key ===null) return
  setData(dataArray)
  setKey(keyArray)
  }, [data, key]);

  return (
    <Doughnut
      data={{
        labels: key,
        datasets: [
          {
            fill: true,
            label: "# of votes",
            data: data,
            borderColor: [
              "rgba(255, 99, 132, 1)",
